import styled from "styled-components";

export const ContainerServices = styled.div`
    display: flex;
    max-width: 80%;
    margin: auto;
    flex-wrap: wrap;
    margin-top: 50px;
    box-sizing: border-box;
`
export const Items = styled.div`
    width: 45%;
    height: 526px;
    background-color:black;
    margin-left: 30px;
    margin-bottom: 100px;
    border-radius: 26px;
    padding: 15px 50px;
    transition: 0.5s;
    text-align: center;
    opacity:1;
    &:hover{
