import styled, { createGlobalStyle } from 'styled-components';
import { GlobalStyleComponent } from 'styled-components';

export const GlobalStyle = createGlobalStyle`
    :root {
        --white: #ffffff;
        --black: #000;
    }
    *{
        margin: 0;
        padding: 0;
        box-sizing:border-box;

    }
    [disabled]{
        opacity: 0.6;
        cursor: not-allowed;
    }
    `

export const Header = styled.header`
    width: 100%;
    background-color: transparent;
    padding: 20px 35px 40px 35px;
    color: var(--black)
`;

export const TextfrontCover = styled.div`
    display: inline-block;
    padding: 254px 0px 0px 90px;
    .titleFront{
        Font family: Roboto;
        Font style: Black;
        Font size: 64px;
        Line height: 75px;
        Line height: 100%;
        Align: Center;
        Vertical align: Top;
    }
`;
