import styled from "styled-components";

export const ContainerServices = styled.div`
    display: flex;
    max-width: 80%;
    margin: auto;
    flex-wrap: wrap;
    margin-top: 150px;
    box-sizing: border-box;
`
export const Items = styled.div`
    width: 45%;
    height: 526px;
    background-color:black; 
    background:
    linear-gradient(
      rgba(0,0,0,.3),rgba(0,0,0,.3)
    ),
    url(https://i.stack.imgur.com/0VvBr.jpg) no-repeat;
    margin-left: 30px;
    margin-bottom: 100px;
    border-radius: 26px;
    padding: 50px;
    cursor: pointer;
    transition: 0.5s;
    opacity:1;
    &:hover{
        opacity: 0.6;
    }
    @media(max-width: 700px) {
        width: 100%;
        margin-left: 0px;
        margin-bottom: 50px;
     }


`
